import cx from 'clsx'
import styles from './Icons.module.scss'

import { useEffect, useRef, useState } from 'react'

const ArrowAnimated = ({ direction = 'down' }: { direction?: 'up' | 'down' }) => {
  const baseIconDown = [
    [' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
    [' ', ' ', ' ', ' ', 'X', ' ', ' ', ' ', 'X', ' ', ' ', ' ', ' '],
    [' ', ' ', ' ', ' ', ' ', 'X', ' ', 'X', ' ', ' ', ' ', ' ', ' '],
    [' ', ' ', ' ', ' ', ' ', ' ', 'X', ' ', ' ', ' ', ' ', ' ', ' '],
    [' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
  ]

  const baseIconUp = [
    [' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
    [' ', ' ', ' ', ' ', ' ', ' ', 'X', ' ', ' ', ' ', ' ', ' ', ' '],
    [' ', ' ', ' ', ' ', ' ', 'X', ' ', 'X', ' ', ' ', ' ', ' ', ' '],
    [' ', ' ', ' ', ' ', 'X', ' ', ' ', ' ', 'X', ' ', ' ', ' ', ' '],
    [' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' ', ' '],
  ]

  const baseIcon = direction === 'down' ? baseIconDown : baseIconUp

  const [icon, setIcon] = useState(baseIcon)
  const [isAnimating, setIsAnimating] = useState(false)
  const animationRef = useRef<NodeJS.Timeout | null>(null)

  const startAnimation = () => {
    if (isAnimating) return

    setIsAnimating(true)

    let frame = 0
    animationRef.current = setInterval(() => {
      setIcon((prev) => {
        if (direction === 'down') {
          const last = prev[prev.length - 1]
          return [last, ...prev.slice(0, -1)]
        } else {
          const first = prev[0]
          return [...prev.slice(1), first]
        }
      })
      frame++

      if (frame >= baseIcon.length) {
        if (animationRef.current) clearInterval(animationRef.current)
        setTimeout(() => {
          setIcon(baseIcon)
          setIsAnimating(false)
        }, 100)
      }
    }, 100)
  }

  useEffect(() => {
    return () => {
      if (animationRef.current) clearInterval(animationRef.current)
    }
  }, [])

  return (
    <div className={styles.navLinkIcon} onClick={startAnimation} onMouseEnter={startAnimation}>
      {icon.map((row, rIdx) => (
        <div key={rIdx} className={styles.navLinkIcon__row}>
          {row.map((cell, cIdx) => (
            <div key={cIdx} className={cx(styles['navLinkIcon__row--node'])} data-is-active={cell === 'X'} />
          ))}
        </div>
      ))}
    </div>
  )
}

// For backwards compatibility, create an ArrowDownAnimated component
const ArrowDownAnimated = () => <ArrowAnimated direction="down" />
const ArrowUpAnimated = () => <ArrowAnimated direction="up" />

